import { styled } from 'styled-components';

export const Container = styled.div`
  margin: 0;
  display: flex;
  flex-direction: column;
  align-items: baseline;

  input {
    background: #ecf0f3;
    box-shadow: 5px 5px 6px 0px #d1d9e6,
      -5px -5px 6px 0px rgba(255, 255, 255, 0.35);
    border-radius: 5px;
  }
`;
